import React from "react";
import profilePicture from "../Assets/porfilePic.png";

class Profile extends React.Component {
  constructor() {
    super();
    this.myStyle = { width: "200px", height: "200px", borderRadius: "100px" };
    this.btnStyle = {
      backgroundColor: "teal",
      color: "white",
      fontSize: "18px",
    };
    this.state = { displayBio: false };
    this.toggle = this.toggle.bind(this); //   to let toggle to Acces all objects
  }
  toggle() {
    if (this.state.displayBio === true) {
      // this.state.displayBio = false;
      this.setState({displayBio: false}); // with this methode render funct
    } else {
      // this.state.displayBio = true;
      this.setState({displayBio: true});
    }
  }
  render() {
    return (
      <div>
        <img src={profilePicture} style={this.myStyle} alt="Profile" />
        <h1>Hello There</h1>
        <p>My Name is Aimad Bouchouaf</p>
        {this.state.displayBio === true ? (
          <div>
            <p>I like to read and football alot</p>
            <p>My best ProgrammingLanguage is Javascript</p>
            <button onClick={this.toggle} style={this.btnStyle}>
              Hide
            </button>
          </div>
        ) : (
          <button onClick={this.toggle} style={this.btnStyle}>
            Show More
          </button>
        )}
      </div>
    );
  }
}

export default Profile;
