import React, { Component } from "react";
import Profile from "./Profile";
import Qualifications from "./Qualifications";
import "../index.css";
import Projects from "./Projects";
import SocialProfiles from "./SocialProfiles";
import Title from "./Title";
import DarkModeToggle from "./DarkModeToggle"; // Import the DarkModeToggle component

class App extends Component {
  constructor() {
    super();
    this.state = {
      darkMode: false, // Initial state for dark mode
    };
    this.toggleDarkMode = this.toggleDarkMode.bind(this);
  }

  toggleDarkMode() {
    this.setState((prevState) => ({
      darkMode: !prevState.darkMode, // Toggle the dark mode state
    }));
  }

  render() {
    const { darkMode } = this.state;
    const appStyle = {
      backgroundColor: darkMode ? "#333" : "#fff", // Adjust background color based on dark mode
      color: darkMode ? "#fff" : "#333", // Adjust text color based on dark mode
      minHeight: "100vh",
      padding: "20px",
    };

    return (
      <div style={appStyle}>
        <DarkModeToggle darkMode={darkMode} toggleDarkMode={this.toggleDarkMode} /> {/* Render the DarkModeToggle component */}
        <div className="row">
          <Title />
          <div className="column">
            <Profile />
          </div>
          <div className="column">
            <Qualifications />
          </div>
        </div>
        <hr />
        <Projects />
        <hr />
        <SocialProfiles />
      </div>
    );
  }
}

export default App;
