import React from "react";
import profilePicture from "../Assets/porfilePic.png";

class Profile extends React.Component {
  constructor() {
    super();

    this.state = { displayBio: false };
    this.toggle = this.toggle.bind(this); //   to let toggle acces all objects
  }
  toggle() {
    // if (this.state.displayBio) {
    //   // this.state.displayBio = false;
    //   this.setState({ displayBio: false }); // with this methode render function will be automaticlly called
    // } else {
    //   // this.state.displayBio = true;
    //   this.setState({ displayBio: true });
    // }
    this.setState({displayBio: !this.state.displayBio})
  }
  render() {
    return (
      <div>
        <img src={profilePicture} className="profile" alt="Profile" />
        <h1>Hello There</h1>
        <p>My Name is Aimad Bouchouaf</p>
        <p>C</p>
        {this.state.displayBio ? (
          <div>

            <p>
As a versatile computer science student with a focus on database management, frontend development, and specification management, I possess extensive knowledge in MySQL, C, C++, PostgreSQL, and object-oriented programming. My bachelor's studies at Bergische Universit√§t Wuppertal form the foundation of my expertise. Practical experience as a Working Student Laboratory at ZWILLING J.A. Henckels LLC has deepened my skills in SQL database queries, frontend setup, and data maintenance. Additionally, I bring solid knowledge of HTML, CSS, and JavaScript, enabling me to develop engaging and user-friendly frontend solutions. My willingness to learn new technologies and concepts, along with my commitment to continuous learning, makes me a valuable member in the field of computer science.</p>
            <button onClick={this.toggle} className="btn">
              Hide
            </button>
          </div>
        ) : (
          <button onClick={this.toggle} className="btn">
            Show More
          </button>
        )}
      </div>
    );
  }
}

export default Profile;
