import React, { Component } from "react";

class Quotes extends Component {
  constructor() {
    super();
    this.state = { quote: {} , quotes: []};

    this.fetchQuotes = this.fetchQuotes.bind
  }

  componentDidMount() {
    fetch("http://localhost:3004/quotes/random")
      .then((response) => response.json())
      .then((json) => this.setState({ quote: json }));
  }

  fetchQuotes(){
    fetch("http://localhost:3004/quotes/ten")
    .then((response) => response.json())
    .then((json) => this.setState({ quotes: json }));
  }

  render() {
    return (
      <div>
        <h2>Today's Quote </h2>
        <p>{this.state.quote.title}</p>
        <hr />
        <h3>Do You Want More Quotes ?</h3>
        <button className="btn" onClick={this.fetchQuotes}>Quotes</button>
        {
          this.state.quotes.map(quotes => {
            return <p>{quotes.title}</p>
          })
        }
      </div>
    );
  }
}

export default Quotes;
