import React from "react";
import COURSES from "../data/courses";

const Courses = () => {
  return (
    <div>
      <h2>My Projects</h2>
      <div>
        {COURSES.map((COURSE) => {
          return <Course course={COURSE} />;
        })}
      </div>
    </div>
  );
};

const Course = (props) => {


    const {title, description, image , link } = props.course; // means     let title = props.course.title;
                                                                        // let description = props.course.description;
                                                                        // let image = props.course.image;
                                                                        // let link = props.course.link;
  return (
    <div className="card">
      <img src={image} alt="Project1 puic" style={{ width: "100%" }} />
      <h3 style={{ color: "grey", fontSize: 20 }}>{title}</h3>
      <p style={{ fontSize: 17, padding: 4 }}>
       {description}
      </p>
      <button onClick={() => {
        window.location.href = link;
      }} >Enroll</button>
    </div>
  );
};

export default Courses;
