import React, { Component } from "react";

class Quotes extends Component {
  constructor() {
    super();
    this.state = { quote: {} };
  }

  componentDidMount() {
    fetch("http://localhost:3004/quotes/random")
      .then((response) => response.json())
      .then((json) => this.setState({ quote: json }));
  }

  fetchQuote(){
    fetch("http://localhost:3004/quotes/ten")
    .then((response) => response.json())
    .then((json) => this.setState({ quote: json }));
  }

  render() {
    return (
      <div>
        <h2>Today's Quote </h2>
        <p>{this.state.quote.title}</p>
        <hr />
        <h3>Do You Want More Quotes ?</h3>
        <button className="btn" onClick={this.fetchQuote}>Quotes</button>
      </div>
    );
  }
}

export default Quotes;
